import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = 'https://pkmnyoyvezizgqzycyuz.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InBrbW55b3l2ZXppemdxenljeXV6Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3MjgyMjU4MDAsImV4cCI6MjA0MzgwMTgwMH0.le8V0aqC8jouFoUC-3XD9bCWqEPXsqInu9buiwzlpKU';
const supabase = createClient(supabaseUrl, supabaseKey);

const conditions = ['A', 'B', 'C'];
const vignettes = {
  'A': ['Vignette A1', 'Vignette A2', 'Vignette A3'],
  'B': ['Vignette B1', 'Vignette B2', 'Vignette B3'],
  'C': ['Vignette C1', 'Vignette C2', 'Vignette C3']
};
const questions = [
  {
    'id': 'q1', 
    'text': 'What is your opinion?', 
    'type': 'likert', 
    'options': ['Strongly Disagree', 'Disagree', 'Neutral', 'Agree', 'Strongly Agree'],
    'required': true
  },
  {
    'id': 'q2', 
    'text': 'Please explain:', 
    'type': 'text',
    'required': false
  }
];

const MIN_COMPLETED_PER_CONDITION = 10;

function App() {
  const [survey, setSurvey] = useState(null);
  const [currentVignetteIndex, setCurrentVignetteIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [isComplete, setIsComplete] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [surveyId, setSurveyId] = useState(null);
  const [isStarted, setIsStarted] = useState(false);
  const [errors, setErrors] = useState({});

  const assignCondition = async () => {
    try {
      // Get counts of started and completed surveys for each condition
      const { data: counts, error: countError } = await supabase
        .from('survey_responses')
        .select('condition, submitted');

      if (countError) throw countError;

      const completedCounts = {};
      const startedCounts = {};
      conditions.forEach(condition => {
        completedCounts[condition] = counts.filter(c => c.condition === condition && c.submitted).length;
        startedCounts[condition] = counts.filter(c => c.condition === condition).length;
      });

      console.log('Completed surveys per condition:', completedCounts);
      console.log('Started surveys per condition:', startedCounts);

      // Find conditions that haven't met the minimum
      const eligibleConditions = conditions.filter(condition => completedCounts[condition] < MIN_COMPLETED_PER_CONDITION);

      let condition;
      if (eligibleConditions.length > 0) {
        // Randomly select from eligible conditions
        condition = eligibleConditions[Math.floor(Math.random() * eligibleConditions.length)];
        console.log('Selected from eligible conditions:', condition);
      } else {
        // If all conditions met minimum, select the one with least completed
        condition = Object.keys(completedCounts).reduce((a, b) => completedCounts[a] <= completedCounts[b] ? a : b);
        console.log('All conditions met minimum. Selected condition with least completed:', condition);
      }

      // Insert the new survey response
      const { data: surveyData, error: surveyError } = await supabase
        .from('survey_responses')
        .insert({ condition: condition, submitted: false })
        .select()
        .single();

      if (surveyError) throw surveyError;

      console.log('New survey assigned to condition:', condition);

      setSurveyId(surveyData.id);
      setSurvey({
        condition,
        vignettes: vignettes[condition],
        questions
      });
      setIsStarted(true);
    } catch (error) {
      console.error('Error assigning condition:', error);
      alert('Error starting survey. Please try again.');
    }
  };

  const handleResponse = (questionId, response) => {
    setResponses(prev => ({
      ...prev,
      [currentVignetteIndex]: { ...prev[currentVignetteIndex], [questionId]: response }
    }));
    // Clear error for this question when answered
    setErrors(prev => ({ ...prev, [questionId]: '' }));
  };

  const validateResponses = () => {
    const currentResponses = responses[currentVignetteIndex] || {};
    let newErrors = {};
    questions.forEach(question => {
      if (question.required && !currentResponses[question.id]) {
        newErrors[question.id] = 'This question is required';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const nextVignette = () => {
    if (validateResponses()) {
      if (currentVignetteIndex < survey.vignettes.length - 1) {
        setCurrentVignetteIndex(currentVignetteIndex + 1);
        // Clear the text input for the open-ended question
        setResponses(prev => ({
          ...prev,
          [currentVignetteIndex + 1]: { ...prev[currentVignetteIndex + 1], q2: '' }
        }));
      } else {
        setIsComplete(true);
      }
    } else {
      alert('Please answer all questions before proceeding.');
    }
  };

  const handleSubmit = async () => {
    if (validateResponses()) {
      try {
        const { error } = await supabase
          .from('survey_responses')
          .update({ 
            responses: responses,
            submitted: true 
          })
          .eq('id', surveyId);

        if (error) throw error;

        console.log('Survey submitted successfully. Condition:', survey.condition);

        setIsSubmitted(true);
      } catch (error) {
        console.error('Error submitting survey:', error);
        alert('Error submitting survey. Please try again.');
      }
    } else {
      alert('Please answer all questions before submitting.');
    }
  };

  if (!isStarted) {
    return (
      <div className="App">
        <h1>Welcome to the Survey</h1>
        <p>Click the button below to start the survey.</p>
        <button onClick={assignCondition}>Start Survey</button>
      </div>
    );
  }

  if (!survey) return <div>Loading...</div>;

  if (isSubmitted) {
    return (
      <div className="App">
        <h1>Survey Submitted</h1>
        <p>Thank you for completing the survey. Your responses have been recorded.</p>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>Survey</h1>
      {!isComplete ? (
        <div>
          <p>{survey.vignettes[currentVignetteIndex]}</p>
          {survey.questions.map(question => (
            <div key={question.id}>
              <p>
                {question.text}
                {question.required && <span style={{ color: 'red' }}> *</span>}
              </p>
              {question.type === 'likert' ? (
                question.options.map(option => (
                  <button 
                    key={option} 
                    onClick={() => handleResponse(question.id, option)}
                    style={{
                      backgroundColor: responses[currentVignetteIndex]?.[question.id] === option ? 'lightblue' : 'white'
                    }}
                  >
                    {option}
                  </button>
                ))
              ) : (
                <textarea 
                  value={responses[currentVignetteIndex]?.[question.id] || ''}
                  onChange={(e) => handleResponse(question.id, e.target.value)} 
                />
              )}
              {errors[question.id] && <p style={{ color: 'red' }}>{errors[question.id]}</p>}
            </div>
          ))}
          <button onClick={nextVignette}>
            {currentVignetteIndex < survey.vignettes.length - 1 ? 'Next' : 'Finish'}
          </button>
        </div>
      ) : (
        <div>
          <p>Thank you for completing the survey. Click submit to record your responses.</p>
          <button onClick={handleSubmit}>Submit Survey</button>
        </div>
      )}
    </div>
  );
}

export default App;
