import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000';

function App() {
  const [condition, setCondition] = useState(null);
  const [vignettes, setVignettes] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [currentVignetteIndex, setCurrentVignetteIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`${API_BASE_URL}/api/assign`)
      .then(response => {
        setCondition(response.data.condition);
        setVignettes(response.data.vignettes);
        setQuestions(response.data.questions);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError(error.message);
      });
  }, []);

  const handleResponse = (questionId, response) => {
    setResponses(prev => ({
      ...prev,
      [currentVignetteIndex]: {
        ...prev[currentVignetteIndex],
        [questionId]: response
      }
    }));
  };

  const nextVignette = () => {
    if (currentVignetteIndex < vignettes.length - 1) {
      setCurrentVignetteIndex(currentVignetteIndex + 1);
    }
  };

  const handleSubmit = () => {
    axios.post(`${API_BASE_URL}/api/submit`, { condition, responses })
      .then(response => {
        alert('Survey submitted successfully!');
      })
      .catch(error => {
        alert('Error submitting survey. Please try again.');
        console.error('Submit error:', error);
      });
  };

  if (error) return <div>Error: {error}</div>;
  if (!condition) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Survey</h1>
      {currentVignetteIndex < vignettes.length ? (
        <div>
          <p>{vignettes[currentVignetteIndex]}</p>
          {questions.map(question => (
            <div key={question.id}>
              <p>{question.text}</p>
              {question.type === 'likert' ? (
                question.options.map(option => (
                  <button key={option} onClick={() => handleResponse(question.id, option)}>
                    {option}
                  </button>
                ))
              ) : (
                <textarea
                  onChange={(e) => handleResponse(question.id, e.target.value)}
                />
              )}
            </div>
          ))}
          <button onClick={nextVignette}>Next</button>
        </div>
      ) : (
        <button onClick={handleSubmit}>Submit Survey</button>
      )}
    </div>
  );
}

export default App;
