import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = 'https://pkmnyoyvezizgqzycyuz.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InBrbW55b3l2ZXppemdxenljeXV6Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3MjgyMjU4MDAsImV4cCI6MjA0MzgwMTgwMH0.le8V0aqC8jouFoUC-3XD9bCWqEPXsqInu9buiwzlpKU';
const supabase = createClient(supabaseUrl, supabaseKey);

const conditions = ['A', 'B', 'C'];
const vignettes = {
  'A': ['Vignette A1', 'Vignette A2', 'Vignette A3'],
  'B': ['Vignette B1', 'Vignette B2', 'Vignette B3'],
  'C': ['Vignette C1', 'Vignette C2', 'Vignette C3']
};
const questions = [
  {'id': 'q1', 'text': 'What is your opinion?', 'type': 'likert', 'options': ['Strongly Disagree', 'Disagree', 'Neutral', 'Agree', 'Strongly Agree']},
  {'id': 'q2', 'text': 'Please explain:', 'type': 'text'}
];

function App() {
  const [survey, setSurvey] = useState(null);
  const [currentVignetteIndex, setCurrentVignetteIndex] = useState(0);
  const [responses, setResponses] = useState({});

  useEffect(() => {
    assignCondition();
  }, []);

  const assignCondition = async () => {
    // Get the current block
    let { data: blocks, error } = await supabase
      .from('blocks')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(1);

    let currentBlock = blocks[0];

    if (!currentBlock || currentBlock.current_index >= conditions.length) {
      // Create a new block if needed
      const newBlock = shuffleArray([...conditions]);
      const { data, error } = await supabase
        .from('blocks')
        .insert({ conditions: newBlock, current_index: 0 })
        .select();
      currentBlock = data[0];
    }

    // Get the next condition and update the index
    const condition = currentBlock.conditions[currentBlock.current_index];
    await supabase
      .from('blocks')
      .update({ current_index: currentBlock.current_index + 1 })
      .eq('id', currentBlock.id);

    // Record the assignment
    await supabase
      .from('condition_assignments')
      .insert({ condition });

    setSurvey({
      condition,
      vignettes: vignettes[condition],
      questions
    });
  };

  const handleResponse = (questionId, response) => {
    setResponses(prev => ({
      ...prev,
      [currentVignetteIndex]: { ...prev[currentVignetteIndex], [questionId]: response }
    }));
  };

  const nextVignette = () => {
    if (currentVignetteIndex < survey.vignettes.length - 1) {
      setCurrentVignetteIndex(currentVignetteIndex + 1);
    }
  };

  const handleSubmit = async () => {
    const { data, error } = await supabase
      .from('survey_responses')
      .insert({ condition: survey.condition, responses });

    if (error) {
      alert('Error submitting survey. Please try again.');
    } else {
      alert('Survey submitted successfully!');
    }
  };

  if (!survey) return <div>Loading...</div>;

  return (
    <div className="App">
      <h1>Survey</h1>
      {currentVignetteIndex < survey.vignettes.length ? (
        <div>
          <p>{survey.vignettes[currentVignetteIndex]}</p>
          {survey.questions.map(question => (
            <div key={question.id}>
              <p>{question.text}</p>
              {question.type === 'likert' ? (
                question.options.map(option => (
                  <button key={option} onClick={() => handleResponse(question.id, option)}>{option}</button>
                ))
              ) : (
                <textarea onChange={(e) => handleResponse(question.id, e.target.value)} />
              )}
            </div>
          ))}
          <button onClick={nextVignette}>Next</button>
        </div>
      ) : (
        <button onClick={handleSubmit}>Submit Survey</button>
      )}
    </div>
  );
}

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

export default App;
